mapper.sql:
	WHERE 1=1 


     	 AND star_no = #{bParam.starNo, jdbcType=VARCHAR}


     	 AND the_event_time = #{bParam.theEventTime, jdbcType=TIMESTAMP}


     	 AND score = #{bParam.score, jdbcType=INTEGER}


     	 AND the_event_time >= #{bParam.luckStart, jdbcType=TIMESTAMP}


     	 AND the_event_time <= #{bParam.luckEnd, jdbcType=TIMESTAMP}






     	 		and score >= 0 and (game_type= 1 or game_type = 2)


     	 		and score < 0 and (game_type= 1 or game_type = 2)


     	 		and game_type= 99 


     	 		and game_type= 0 


     	 		and game_type= 2 


     	 		and game_type= 1 


     	 		and game_type= 3

mapper.select:
select 

from gold_log 


     order by ${pParam.orderByClause} 

    		order by id  desc 


      limit ${pParam.startIndex} , ${pParam.pageSize}



     order by create_date desc
