mapper.sql:
	WHERE 1=1 


     	 AND order_id = #{bParam.orderId, jdbcType=VARCHAR}


     	 AND starNO = #{bParam.starNo, jdbcType=VARCHAR}


     	 AND create_time = #{bParam.createTime, jdbcType=TIMESTAMP}


     	 AND rmb = #{bParam.rmb, jdbcType=INTEGER}


     	 AND goldMoney = #{bParam.goldMoney, jdbcType=INTEGER}


     	 AND state = #{bParam.state, jdbcType=INTEGER}


     	 AND trans_time = #{bParam.transTime, jdbcType=TIMESTAMP}


     	 AND re_marks = #{bParam.reMarks, jdbcType=VARCHAR}


     	 AND trans_player = #{bParam.transPlayer, jdbcType=INTEGER}


     	 AND pay_type = #{bParam.payType, jdbcType=INTEGER}


     	 AND pay_account = #{bParam.payAccount, jdbcType=VARCHAR}

mapper.select:
select 

from order 


     order by ${pParam.orderByClause} 

    		order by id  desc 


      limit ${pParam.startIndex} , ${pParam.pageSize}



     order by create_date desc
