mapper.sql:
	 	WHERE 1=1 

             AND user_id = #{bParam.userId,jdbcType=INTEGER} 
             AND login_name like concat(concat('%',#{bParam.loginName,jdbcType=VARCHAR}),'%') 
             AND star_no like concat(concat('%',#{bParam.starNo,jdbcType=VARCHAR}),'%') 
             AND nick like concat(concat('%',#{bParam.nick,jdbcType=VARCHAR}),'%') 
             AND user_status = #{bParam.userStatus,jdbcType=INTEGER} 
             AND create_time = #{bParam.createTime,jdbcType=TIMESTAMP} 
             AND update_time = #{bParam.updateTime,jdbcType=TIMESTAMP}

mapper.select:
select 

from system_users




     order by ${pParam.orderByClause} 
     order by create_time desc 

      limit #{pParam.startIndex} , #{pParam.pageSize}



     order by create_date desc
