mapper.sql:
	WHERE (c_state=1 or c_state=0 )



     	 AND (club.name like concat(concat('%',#{bParam.name,jdbcType=VARCHAR}),'%') 
     	 or club.c_no like concat(concat('%',#{bParam.name,jdbcType=VARCHAR}),'%') 
     	 or system_users.star_no like concat(concat('%',#{bParam.name,jdbcType=VARCHAR}),'%') 
     	 or system_users.nick like concat(concat('%',#{bParam.name,jdbcType=VARCHAR}),'%') 
     	 ) 


     	 AND c_create_time = #{bParam.ccreateTime, jdbcType=TIMESTAMP}


     	 AND c_state = #{bParam.cstate, jdbcType=INTEGER}

mapper.select:
select 

from club 




     order by ${pParam.orderByClause} 

    		order by c_id  desc 


      limit ${pParam.startIndex} , ${pParam.pageSize}



     order by create_date desc

SELECT 
    FROM club 

    WHERE 1=1

  	 	and c_user_id = #{cuserId} 


  	 	and c_id != #{cid}
