mapper.sql:
	WHERE 1=1 


     	 AND sr_city_id = #{bParam.srCityId, jdbcType=INTEGER}


     	 AND sr_create_time = #{bParam.srCreateTime, jdbcType=TIMESTAMP}


     	 AND create_roomcards = #{bParam.createRoomcards, jdbcType=INTEGER}


     	 AND market_roomcards = #{bParam.marketRoomcards, jdbcType=INTEGER}


     	 AND consume_roomcards = #{bParam.consumeRoomcards, jdbcType=INTEGER}


     	 AND give_out_roomcards = #{bParam.giveOutRoomcards, jdbcType=INTEGER}

mapper.select:
select 

from statistics_roomcard 


     order by ${pParam.orderByClause} 

    		order by sr_id  desc 


      limit ${pParam.startIndex} , ${pParam.pageSize}



     order by create_date desc
