mapper.sql:
	WHERE 1=1 


     	 AND agency_state = #{bParam.agencyState, jdbcType=INTEGER}


     	 AND sa_create_time = #{bParam.saCreateTime, jdbcType=TIMESTAMP}


     	 AND sa_less_one = #{bParam.saLessOne, jdbcType=DOUBLE}


     	 AND sa_one_to_two = #{bParam.saOneToTwo, jdbcType=DOUBLE}


     	 AND sa_two_to_three = #{bParam.saTwoToThree, jdbcType=DOUBLE}


     	 AND sa_three_to_four = #{bParam.saThreeToFour, jdbcType=DOUBLE}


     	 AND sa_greater_foure = #{bParam.saGreaterFoure, jdbcType=DOUBLE}


     	  AND( system_users.nick like concat(concat('%',#{bParam.seacherParm,jdbcType=VARCHAR}),'%') 
     	  or city.name like concat(concat('%',#{bParam.seacherParm,jdbcType=VARCHAR}),'%') 
     	  )

mapper.select:
select
from system_agency LEFT JOIN system_users ON system_agency.sa_user_id = system_users.user_id LEFT JOIN city ON system_agency.sa_city_id = city.id
order by ${pParam.orderByClause}
order by sa_id desc
limit ${pParam.startIndex} , ${pParam.pageSize}
order by create_date desc

SELECT 
    FROM system_agency 

    WHERE 1=1

  	 	and sa_user_id = #{saUserId} 


  	 	and sa_id != #{saId}
