mapper.select:
SELECT s.id AS id, s.purchase_type AS purchaseType, s.order_no AS orderNo, u.`name`, s.user_id AS userId, p.nick, s.start_no AS startNo, s.pay_price AS payPrice, s.pay_type AS payType, s.pay_status AS payStatus, s.pay_time AS payTime, s.roomcard_price AS roomcardPrice, s.roomcard_amount AS roomcardAmount, s.create_time AS createTime FROM system_order s LEFT JOIN `user` u ON s.user_id = u.id LEFT JOIN player p ON s.start_no = p.start_no
1=1
and s.start_no like concat(concat('%',#{startNo}),'%')
and s.purchase_type = #{purchaseType }
and s.pay_type = #{payType }
GROUP BY s.id
